mapper.insert:
insert into growth_item
(
growth_id
, growth_type
, image_url
, progress_id
, reg_datetime
, mod_datetime
)
values
(
#{growthId}
, #{growthType}
, #{imageUrl}
, #{progressId}
, sysdate()
, sysdate()
)

    select last_insert_id()

mapper.delete:
delete from growth_item
where 1 = 1
and item_id = #{itemId}
and growth_id = #{growthId}
