mapper.insert:
        insert into growth
        (
        child_id
        , grow_date
        , stature
        , weight
        , note
        , status
        , reg_datetime
        , mod_datetime
        )
        values
        (
        #{childId}
        , #{growDate}
        , #{stature}
        , #{weight}
        , #{note}
        , 'S'
        , sysdate()
        , sysdate()
        )

        
            select last_insert_id()

mapper.update:
update user
set
child_id = #{childId}
, grow_date = #{growDate}
, stature = #{stature}
, weight = #{weight}
, note = #{note}
, status = #{status}
, mod_datetime = sysdate()
where 1 = 1
and growth_id = #{growthId}

mapper.delete:
delete from growth
where 1 = 1
and growth_id = #{growthId}
