mapper.insert:
        insert into user
        (
              name
            , email
            , password
            , phone
            , user_status
            , reg_datetime
            , mod_datetime
        )
        values
        (
              #{name}
            , #{email}
            , #{password}
            , #{phone}
            , #{userStatus}
            , sysdate()
            , sysdate()
        )

        
            select last_insert_id()

mapper.update:
update user
set
  name = #{name}
, email = #{email}
, password = #{password}
, phone = #{phone}
, user_status = #{userStatus}
, mod_datetime = sysdate()
where 1 = 1
and user_id = #{userId}

mapper.delete:
delete from user
where 1 = 1
and user_id = #{userId}
